SELECT hidden_progress, COUNT(*) count FROM prevention_hidden_register
dept_id = #{deptId}
GROUP BY hidden_progress;

SELECT t1.task_state, COUNT(*) count FROM prevention_check_task t1 LEFT JOIN prevention_check_task_config t2 ON t1.check_task_config_id = t2.id LEFT JOIN prevention_security_risk t3 ON t2.security_risk_id = t3.id LEFT JOIN prevention_device t4 ON t3.device_id = t4.id
t4.dept_id = #{deptId}
GROUP BY t1.task_state;
