mapper.select:
SELECT  FROM sys_hidden_danger


        and area_name = #{areaName}


        AND problem = #{problem}


        AND file_id = #{fileId}


        AND area_id = #{areaId}


        AND problem_id = #{problemId}


        AND task_type = #{taskType}


        AND change_date = #{changeDate}


        AND month = #{month}


        AND week = #{week}


        AND duty_unit_id = #{dutyUnitId}


        AND duty_unit = #{dutyUnit}


        AND duty_person = #{dutyPerson}


        AND change_measure = #{changeMeasure}


        AND change_describe = #{changeDescribe}


        AND problem_name = #{problemName}


        AND status = #{status}


        AND del_flag = #{delFlag}


        AND create_by = #{createBy}


        AND update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and create_time >=#{stateDate} AND  create_time <=#{endDate}


order by id desc

mapper.update:
UPDATE sys_hidden_danger
file_id = #{fileId}, file_name = #{fileName}, file_url = #{fileUrl}, area_id = #{areaId}, area_name = #{areaName}, problem_id = #{problemId}, problem = #{problem}, task_type = #{taskType}, status = #{status}, change_date = #{changeDate}, duty_unit_id = #{dutyUnitId}, duty_unit = #{dutyUnit}, duty_person = #{dutyPerson}, change_measure = #{changeMeasure}, change_describe = #{changeDescribe}, problem_name = #{problemName}, del_flag = #{delFlag}, update_by = #{updateBy}, update_time = sysdate()
WHERE id = #{id}

UPDATE sys_hidden_danger

    examine_idea = #{examineIdea},
    examine_verify_date = #{examineVerifyDate},
    status = #{status}

WHERE id = #{id}

UPDATE sys_hidden_danger
file_name = #{fileName}, file_url = #{fileUrl}, change_describe = #{changeDescribe}, status = #{status},
WHERE id = #{id}
